function iniciarValidacion(){validarForm()}function validarForm(){const a=document.querySelectorAll(".needs-validation");Array.from(a).forEach(a=>{a.addEventListener("submit",i=>{i.preventDefault(),a.checkValidity()||i.stopPropagation(),a.classList.add("was-validated")},!1)})}document.addEventListener("DOMContentLoaded",(function(a){iniciarValidacion()}));